document.querySelectorAll(".dropdown").forEach(function(e){const t=e.querySelector(".dropdown__button"),o=e.querySelector(".dropdown__list"),i=e.querySelectorAll(".dropdown__list-item"),n=e.querySelector(".dropdown__input_hidden");console.log(t),t.addEventListener("click",function(){o.classList.toggle("dropdown__list_visible"),this.classList.toggle("dropdown__button_active")}),i.forEach(function(e){e.addEventListener("click",function(e){i.forEach(function(e){e.classList.remove("dropdown__list-item_active")}),e.target.classList.add("dropdown__list-item_active"),t.innerText=this.innerText,n.value=this.dataset.value,o.classList.remove("dropdown__list_visible")})}),document.addEventListener("click",function(e){e.target!==t&&(t.classList.remove("dropdown__button_active"),o.classList.remove("dropdown__list_visible"))}),document.addEventListener("keydown",function(e){"Tab"!==e.key&&"Escape"!==e.key||(t.classList.remove("dropdown__button_active"),o.classList.remove("dropdown__list_visible"))})});